CREATE TABLE NOTE (BACKEND_ID VARCHAR,ID INTEGER PRIMARY KEY AUTOINCREMENT,LATITUDE DOUBLE PRECISION NOT NULL,LONGITUDE DOUBLE PRECISION NOT NULL,TEXT VARCHAR,STATUS VARCHAR,CREATED_DATE BIGINT,UPDATED SMALLINT NOT NULL);
CREATE TABLE COMMENT (ID INTEGER PRIMARY KEY AUTOINCREMENT,TEXT VARCHAR,'ACTION' VARCHAR,CREATED_DATE BIGINT,UPDATED SMALLINT NOT NULL,NOTE_ID BIGINT NOT NULL);